import UIKit

// MARK: Connect View, Interactor, and Presenter

extension RankingInteractor: RankingViewControllerOutput, RankingRouterDataSource, RankingRouterDataDestination {
}

extension RankingPresenter: RankingInteractorOutput {
}

class RankingConfigurator {
    // MARK: Object lifecycle
    
    static let sharedInstance = RankingConfigurator()
    
    private init() {}
    
    // MARK: Configuration
    
    func configure(viewController: RankingViewController) {
        
        let presenter = RankingPresenter()
        presenter.output = viewController
        
        let interactor = RankingInteractor()
        interactor.output = presenter
        
        let router = RankingRouter(viewController: viewController, dataSource: interactor, dataDestination: interactor)
        
        viewController.output = interactor
        viewController.router = router
    }
}
